import React from 'react';
import { AppStateProvider, useAppState } from './components/AppStateContext';
import CodeEditor from './components/CodeEditor';
import ErrorsPanel from './components/ErrorsPanel';
import ConsolePanel from './components/ConsolePanel';

import './App.css';

function App() {
  // This component is now responsible for rendering the button and handling its click event
  return (
    <AppStateProvider>
      <div className="app-container">
        <CodeEditor />
        <RunCodeButton /> {/* Render the button component */}
        <div className="side-panels">
          <ErrorsPanel />
          <ConsolePanel />
        </div>
      </div>
    </AppStateProvider>
  );
}

// Separate component for the button to use the useAppState hook correctly
function RunCodeButton() {
  const { code, setConsoleOutput } = useAppState();

  function executeCode() {
    const originalConsoleLog = console.log;
    let consoleOutput = '';

    console.log = (...args) => {
      consoleOutput += args.join(' ') + '\n';
      originalConsoleLog.apply(console, args);
    };

    try {
      eval(code);
    } catch (error) {
      consoleOutput += `Error: ${error.message}`;
    }

    setConsoleOutput(consoleOutput);
    console.log = originalConsoleLog;
  }

  return <button onClick={executeCode}>Run Code</button>;
}


export default App;
