import '../styles/CodeEditor.css';
import React, { useRef, useEffect } from 'react';
import AceEditor from 'react-ace';
import 'ace-builds/src-noconflict/mode-javascript';
import 'ace-builds/src-noconflict/theme-monokai';
import { useAppState } from './AppStateContext';

function CodeEditor({ focused }) { // Add the focused prop
  const { code, setCode } = useAppState();
  const aceEditorRef = useRef();
  const markers = useRef({});

  useEffect(() => {
    const editor = aceEditorRef.current?.editor;

    if (editor && focused) { // Check if the editor is focused
      // Add marker and navigation commands only if the editor is focused

      // Ctrl + , = Drop marker 1
      editor.commands.addCommand({
        name: 'dropMarker1',
        bindKey: { win: 'Ctrl-,', mac: 'Ctrl-,' },
        exec: (editor) => {
          markers.current['marker1'] = editor.getCursorPosition();
          console.log("m1 dropped at : " + editor.getCursorPosition())
        }
      });

      // Option + , = Jump to marker 1
      editor.commands.addCommand({
        name: 'jumpToMarker1',
        bindKey: { win: 'Alt-,', mac: 'Option-,' },
        exec: (editor) => {
          const position = markers.current['marker1'];
          if (position) {
            editor.moveCursorToPosition(position);
            console.log("going to m1 at : " + position)
          }
        }
      });

      // Ctrl + , = Drop marker 2
      editor.commands.addCommand({
        name: 'dropMarker2',
        bindKey: { win: 'Ctrl-.', mac: 'Ctrl-.' },
        exec: (editor) => {
          markers.current['marker2'] = editor.getCursorPosition();
          console.log("m2 dropped at : " + editor.getCursorPosition())
        }
      });

      // Option + , = Jump to marker 2
      editor.commands.addCommand({
        name: 'jumpToMarker2',
        bindKey: { win: 'Alt-.', mac: 'Option-.' },
        exec: (editor) => {
          const position = markers.current['marker2'];
          if (position) {
            editor.moveCursorToPosition(position);
            console.log("going to m2 at : " + position)
          }
        }
      });

      // Command + 1: Move to start
      editor.commands.addCommand({
        name: 'moveToStart',
        bindKey: { win: 'Ctrl-1', mac: 'Option-1' },
        exec: (editor) => {
          editor.gotoLine(1, 0, false);
        }
      });

      // Command + 2: Move to middle
      editor.commands.addCommand({
        name: 'moveToMiddle',
        bindKey: { win: 'Ctrl-2', mac: 'Option-2' },
        exec: (editor) => {
          const middleLine = Math.floor(editor.session.getLength() / 2);
          editor.gotoLine(middleLine, 0, false);
        }
      });

      // Command + 3: Move to end
      editor.commands.addCommand({
        name: 'moveToEnd',
        bindKey: { win: 'Ctrl-3', mac: 'Option-3' },
        exec: (editor) => {
          const lastLine = editor.session.getLength();
          editor.gotoLine(lastLine, 0, false);
        }
      });

      editor.commands.addCommand(dropMarker1);
      editor.commands.addCommand(jumpToMarker1);
      editor.commands.addCommand(dropMarker2);
      editor.commands.addCommand(jumpToMarker2);
      editor.commands.addCommand(moveToStart);
      editor.commands.addCommand(moveToMiddle);
      editor.commands.addCommand(moveToEnd);


      return () => {
        // Remove commands when the editor loses focus or the component unmounts
        editor.commands.removeCommand('dropMarker1');
        editor.commands.removeCommand('jumpToMarker1');
        editor.commands.removeCommand('dropMarker2');
        editor.commands.removeCommand('jumpToMarker2');
        editor.commands.removeCommand('moveToStart');
        editor.commands.removeCommand('moveToMiddle');
        editor.commands.removeCommand('moveToEnd');

      };
    }
  }, [focused]); // Re-run the effect when the focused prop changes

  function handleCodeChange(newCode) {
    setCode(newCode);
  }

  function moveToLine(lineNumber) {
    if (aceEditorRef.current) {
      aceEditorRef.current.editor.gotoLine(lineNumber, 0, true);
    }
  }

  return (
    <div style={{ flex: 2 }}>
      <AceEditor
        ref={aceEditorRef}
        mode="javascript"
        theme="monokai"
        name="UNIQUE_ID_OF_DIV"
        value={code}
        onChange={handleCodeChange}
        editorProps={{ $blockScrolling: true }}
        style={{ width: '100%', height: '100%' }}
      />
    </div>
  );
}

export default CodeEditor;
